import React,{Component} from 'react' ;
import axios from 'axios' ;


class Home extends Component{

  state = {
    posts : [] 
  }
  

componentDidMount(){




axios.get('https://jsonplaceholder.typicode.com/posts')
.then(response => {
 // console.log(response.data.slice(0,8));

this.setState({
    posts :  response.data.slice(0,8)
    });

    console.log(this.state.posts);
  } 
 );



}


render() {
    

  const { posts } = this.state ;
  const postList = posts.length ? (posts.map(post=>{

    return(
      <div className="col s6 m6 l6 json-card">
      <div className="card" key={post.id}>
      <div className="card-content">
    <span className="card-title"> <h6> <strong> {post.title} </strong> </h6></span>
    <p> {post.body }</p>
        <a className="waves-effect waves-light btn card-btn"><i class="material-icons left">call</i>Call Now</a>
      </div>
      
    </div>
    </div>

    );
    
  }) ) : (
      <div className="center"> <h5> No Posts yet </h5></div>
    ) 



return(



    <div>
      <div className="row  ">
      <div className="col s12 main-column "> 
        
        <div className="col s12 m4 l4  ">
          <div className="brand-text"> 
          <h3 className="center-align ">Hello Visitor </h3>
         <h5 className="center"> Welcome to Shop Developed in React ! <br/>  We are glad to serve you ! </h5>
         <a className="waves-effect waves-light btn call-to-action"><i class="material-icons left">call</i>Call Now</a>
        
         </div> 
        </div>
        </div>


      </div>

      <div className="container">
          <div className="row"> 
         
          {postList}
                    </div>
          </div>







      
    </div>

    );
        }
}

export default Home ;
